import React, {Fragment,useState,useEffect} from 'react';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {Link,useNavigate} from 'react-router-dom';
import {loginRoute} from '../resources/APIkeys';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
const Login =()=>{
  const[values,setValues] = useState({
    userName: "",
    password: "",
    
})
useEffect( ()=>{
  if(localStorage.getItem(process.env.REACT_APP_LOCALHOST_KEY)){
    navigate("/");
  }
  
},[]);
const onChangeHandler = (event) => {
 
  setValues({
      ...values,[event.target.name]:event.target.value
  });
};
const formValidation =() => {

 const {userName,password} = values;
 
 
 if(password===""){
   toast.error("enter the password",toastOptions);
   return false;
 }
 else if(userName===""){
  toast.error("enter the username",toastOptions);
  return false;
 }
 
 else if(userName.length<6){
   toast.error("username is too small", toastOptions);
   return false;
 }


 
   return true;
 
}

const loginSubmitHandler = async(event)=> {


  event.preventDefault();
  
    if(formValidation()){
      console.log("we are here");
      
      const {userName,password}= values;
      const response = await fetch(loginRoute,{
        method: "POST",
         body: JSON.stringify({
         userName,
         password,
    
  }),
  headers: {
    "Content-Type": "application/json",
  },
 });
    try{
      if(response.ok){
        console.log("we are here");
        const data = await response.json();
        console.log(data);
        if(data.status===false){
          toast.error(data.msg, toastOptions);
        }
        if(data.status===true){
            
          localStorage.setItem(process.env.REACT_APP_LOCALHOST_KEY,JSON.stringify(data.userNameCheck));
          navigate("/");
        }
      }
     
    }catch(err){
    console.log(err);
  }

}
 
 
};
  return(
    <Fragment>
      
      
    <Form class="d-flex align-items-center flex-column w-100">

    <Form.Group className="mb-3" class="w-25 p-3" controlId="formBasicEmail">
      <Form.Label>Email address</Form.Label>
      <Form.Control type="email" placeholder="Enter email" />
    </Form.Group>

    <Form.Group className="mb-3" class="w-25 p-3" controlId="formBasicPassword">
      <Form.Label>Password</Form.Label>
      <Form.Control type="password" placeholder="Password" />
    </Form.Group>
    <Form.Group className="mb-3" class="w-25 p-2" controlId="formBasicCheckbox">
    </Form.Group>
    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>
         <ToastContainer />
    </Fragment>
  )
}
export default Login;
