import React from 'react';
import {Fragment,useState,useEffect} from 'react';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {Link,useNavigate} from 'react-router-dom';
import {registerRoute} from '../Resources/ApiKeys';


const Register =()=>{
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };


    const[values,setValues] = useState({
        userName: "",
        email: "",
        password: "",
        confirmPassword: ""
    })
    useEffect( ()=>{
      if(localStorage.getItem("new-user")){
        navigate("/");
      }
      
    },[]);
    const onChangeHandler = (event) => {
     
      setValues({
          ...values,[event.target.name]:event.target.value
      });
};
    const formValidation =() => {

     const {userName,email,password,confirmPassword} = values;
    
      if(userName===""){
      toast.error("enter the username",toastOptions);
      return false;
     }
     else if(userName.length<6){
      toast.error("username is too small", toastOptions);
      return false;
    }
    else if(email===""){
      toast.error("enter the email", toastOptions);
      return false;
    }
    else if(password.length<8){
      toast.error("password is too weak",toastOptions);
      return false;
    }
    else if(password!==confirmPassword){
       toast.error("enter the right password",toastOptions);
       return false;
     }
       return true;
     
    }
   
    const formSubmitHandler = async(event)=> {

    
      event.preventDefault();
      
        if(formValidation()){
          // console.log("we are here");
          
          const {userName,email,password}= values;
          const response = await fetch(registerRoute,{
            method: "POST",
             body: JSON.stringify({
             userName,
             email,
             password,
        
      }),
      headers: {
        "Content-Type": "application/json",
      },
     });
        try{
          if(response.ok){
            console.log("we are here");
            const data = await response.json();
            console.log(data);
            if(data.status===false){
              toast.error(data.msg, toastOptions);
            }
            if(data.status===true){
              localStorage.setItem("new-user",JSON.stringify(data.user));
              navigate("/avatar");
            }
          }
         
        }catch(err){
        console.log(err);
      }

    }
  }
  return(
    <Fragment>
      
      <form>
   
    <h1 class="h3 mb-3 fw-normal">Please sign in</h1>

    <div class="form-floating">
      <input type="email" class="form-control" id="floatingInput" placeholder="name@example.com" />
      <label for="floatingInput">Email address</label>
    </div>
    <div class="form-floating">
      <input type="password" class="form-control" id="floatingPassword" placeholder="Password" />
      <label for="floatingPassword">Password</label>
    </div>

    <div class="checkbox mb-3">
      <label>
        <input type="checkbox" value="remember-me"> Remember me </input>
      </label>
    </div>
    <button class="w-100 btn btn-lg btn-primary" type="submit">Sign in</button>
    <p class="mt-5 mb-3 text-muted">&copy; 2017–2022</p>
  </form>
           <ToastContainer />
      </Fragment>
  )
}
export default Register;
