import React from 'react';
import {Fragment,useState,useEffect} from 'react';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {Link,useNavigate} from 'react-router-dom';
import {registerRoute} from '../Resources/ApiKeys';
import Navbar from '../Utilities/Navbar';

const Register =()=>{
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };


    const[values,setValues] = useState({
        userName: "",
        email: "",
        password: "",
        confirmPassword: ""
    })
    useEffect( ()=>{
      if(localStorage.getItem("new-user")){
        navigate("/");
      }
      
    },[]);
    const onChangeHandler = (event) => {
     
      setValues({
          ...values,[event.target.name]:event.target.value
      });
};
    const formValidation =() => {

     const {userName,email,password,confirmPassword} = values;
    
      if(userName===""){
      toast.error("enter the username",toastOptions);
      return false;
     }
     else if(userName.length<6){
      toast.error("username is too small", toastOptions);
      return false;
    }
    else if(email===""){
      toast.error("enter the email", toastOptions);
      return false;
    }
    else if(password.length<8){
      toast.error("password is too weak",toastOptions);
      return false;
    }
    else if(password!==confirmPassword){
       toast.error("enter the right password",toastOptions);
       return false;
     }
       return true;
     
    }
   
    const formSubmitHandler = async(event)=> {

    
      event.preventDefault();
      
        if(formValidation()){
          // console.log("we are here");
          
          const {userName,email,password}= values;
          const response = await fetch(registerRoute,{
            method: "POST",
             body: JSON.stringify({
             userName,
             email,
             password,
        
      }),
      headers: {
        "Content-Type": "application/json",
      },
     });
        try{
          if(response.ok){
            console.log("we are here");
            const data = await response.json();
            console.log(data);
            if(data.status===false){
              toast.error(data.msg, toastOptions);
            }
            if(data.status===true){
              localStorage.setItem("new-user",JSON.stringify(data.user));
              navigate("/avatar");
            }
          }
         
        }catch(err){
        console.log(err);
      }

    }
  }
  return(
    <Fragment>
      <Navbar />
      <form>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label text-center">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" id="exampleInputPassword1" />
  </div>
  <div className="mb-3 form-check">
    <input type="checkbox" className="form-check-input" id="exampleCheck1" />
    <label className="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
      </Fragment>
  )
}
export default Register;
