import React, { Fragment ,useState} from "react";
// import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
// import Form from "react-bootstrap/Form";
// import InputGroup from "react-bootstrap/InputGroup";
import ContactSearch from "../Utilities/ContactSearch";
import MyNavbar from "../Utilities/MyNavbar"
import Modals from "../Utilities/Modals"
const ContactList = () => {

  const [show,setShow] = useState(false);

  return (
    <Fragment>
      <MyNavbar />
      <Container className="d-flex flex-column pt-4 pb-3">
        <Container className="d-flex justify-content-center">
        <Button className = "w-25" onClick = {()=>{setShow(true)}}>ADD CONTACT</Button>
        {show && <Modals show = {show} onClose = {()=>{setShow(false)}} />}
        </Container>
        
      <ContactSearch />
      </Container>
    </Fragment>
  );
};
export default ContactList;
