import React,{Fragment,useState,useEffect} from 'react';
import axios from 'axios'
import { showdataRoute } from '../Resources/ApiKeys';
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";

const TransactionCell = (props) => {
  return (
    <li>
      <span>{props.payload.name}</span>
      <span>${props.payload.phone}</span>
      <span>${props.payload.email}</span>
    </li>
  );
};
const ContactSearch = (props)=>{
 
  
 const[searchText,setSearchText] = useState("");
 const [filteredTransaction, updateTxn] = useState(props.contacts);
 props.contacts.sort((a, b) => {
  let fa = a.name.toLowerCase(),
      fb = b.name.toLowerCase();

  if (fa < fb) {
      return -1;
  }
  if (fa > fb) {
      return 1;
  }
  return 0;
});

 const filterData = (searchText) => {
   if (!searchText || !searchText.trim().length) {
     updateTxn(props.contacts);
     return;
   }
   let txn = [...props.contacts];
   txn = txn.filter((payload) =>
     (payload.name).toLowerCase().includes(searchText.toLowerCase().trim()),
   );
   updateTxn(txn);
 };

 useEffect(() => {
   filterData(searchText);
 }, [props.contacts]);
 
  
 const onChangeHandler = (event)=>{
  event.preventDefault();
  setSearchText(event.target.value);
 }


  return(
    <Fragment>
      <Container className = "d-flex  justify-content-center">
      <InputGroup className="mt-3 w-25 mx-3">
        <Form.Control 
          onChange={(e) => {
            setSearchText(e.target.value);
            filterData(e.target.value);
          }}
          placeholder="Search Contact"
          aria-label="Username"
          aria-describedby="basic-addon1"
        />
      </InputGroup>
      {filteredTransaction?.map((payload) => (
        <TransactionCell payload={payload} />
      ))}
      </Container>
     
       
    </Fragment> 
  )
}
export default ContactSearch;
