import React from 'react';
import {Fragment,useState,useEffect} from 'react';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {Link,useNavigate} from 'react-router-dom';
import {registerRoute} from '../Resources/ApiKeys';
import MyNavbar from '../Utilities/MyNavbar';
import Button from 'react-bootstrap/esm/Button';
import Form from 'react-bootstrap/esm/Form';
import Container from 'react-bootstrap/esm/Container';

const Register =()=>{ 
  const navigate = useNavigate();
  const toastOptions = {
    position: "bottom-right",
    autoClose: 8000,
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  };


    const[values,setValues] = useState({
        userName: "",
        email: "",
        password: "",
        confirmPassword: ""
    })
    useEffect( ()=>{
      if(localStorage.getItem("new-user")){
        navigate("/");
      }
      
    },[]);
    const onChangeHandler = (event) => {
     
      setValues({
          ...values,[event.target.name]:event.target.value
      });
};
    const formValidation =() => {

     const {userName,email,password,confirmPassword} = values;
    
      if(userName===""){
      toast.error("enter the username",toastOptions);
      return false;
     }
     else if(userName.length<6){
      toast.error("username is too small", toastOptions);
      return false;
    }
    else if(email===""){
      toast.error("enter the email", toastOptions);
      return false;
    }
    else if(password.length<8){
      toast.error("password is too weak",toastOptions);
      return false;
    }
    else if(password!==confirmPassword){
       toast.error("enter the right password",toastOptions);
       return false;
     }
       return true;
     
    }
   
    const formSubmitHandler = async(event)=> {

    
      event.preventDefault();
      
        if(formValidation()){
          // console.log("we are here");
          
          const {userName,email,password}= values;
          const response = await fetch(registerRoute,{
            method: "POST",
             body: JSON.stringify({
             userName,
             email,
             password,
        
      }),
      headers: {
        "Content-Type": "application/json",
      },
     });
        try{
          if(response.ok){
            console.log("we are here");
            const data = await response.json();
            console.log(data);
            if(data.status===false){
              toast.error(data.msg, toastOptions);
            }
            if(data.status===true){
              localStorage.setItem("new-user",JSON.stringify(data.user));
              navigate("/avatar");
            }
          }
         
        }catch(err){
        console.log(err);
      }

    }
  }
  return(
    <Fragment>
      <MyNavbar page = {"Login"} />
      <Container className="d-flex w-75 justify-content-center mt-3">
      <Form className = "w-35" >
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Enter email" />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control type="password" placeholder="Password" />
      </Form.Group>
      
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Re-enter Password</Form.Label>
        <Form.Control type="password" placeholder="Re-enter Password" />
      </Form.Group>
       
      <Container>
      <Button className=' d-flex' variant="primary" type="submit">
        Submit
      </Button>
      </Container>
      
    </Form>
      </Container>

      
  
      </Fragment>
  )
}
export default Register;
